<form class="form-horizontal m-t-20" action="Register_teacher_complete" method="POST"><div class="form-group row"><div class="col-6"><input class="form-control" type="text" placeholder="ชื่อ" name="Frist_name" id="Frist_name" required></div><div class="col-6"><input class="form-control" type="text" placeholder="นามสกุล" name="last_name" id="last_name" required></div></div><div class="form-group row"><div class="col-12"><input class="form-control" type="text" placeholder="รหัสอาจารย์" name="code_student" id="username" required></div></div><div class="form-group row"><div class="col-6"><input class="form-control" type="password" placeholder="รหัสผ่าน" name="password" id="password" required></div><div class="col-6"><input class="form-control" type="password" placeholder="ยืนยันรหัสผ่าน" name="cpassword" id="confirm_password" required></div><span id="message" style="margin-left: 14px;"></span></div><div class="form-group row"><div class="col-12"><input class="form-control" type="email" placeholder="อีเมล" name="email" id="email" required></div></div><div class="form-group text-center row m-t-20"><div class="col-6"><button class="btn btn-success btn-block waves-effect waves-light" type="submit">สมัคร</button></div><div class="col-6"><a href="Login"><button class="btn btn-basic btn-block waves-effect waves-light" type="button">เข้าสู่ระบบ</button></a></div></div></form>